// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.8.2 <0.9.0;

contract DonationManager
{
    struct Donation {
        uint256 donation_id;
        uint256 campaignId;
        address donor;
        uint256 amount;
        string proofHash; // IPFS hash of screenshot/receipt
        uint256 timestamp;
    }

    function donate (uint256 campaignId, uint256 amount, string memory proofHash) public view
    {
        Donation memory newdonate;
        newdonate.campaignId=campaignId;
        newdonate.donor=msg.sender;
        newdonate.amount=amount;
        newdonate.proofHash=proofHash;
        newdonate.timestamp=block.timestamp;
    }

    // function getDonationsByDonor ()
